-- ----------------------------
-- Table structure for think_img
-- ----------------------------
DROP TABLE IF EXISTS `think_img`;
CREATE TABLE `think_img`  (
  `img_id` int(11) NOT NULL AUTO_INCREMENT,
  `img_title` varchar(255) CHARACTER SET utf8 COLLATE utf8_general_ci NOT NULL,
  `click_num` int(10) UNSIGNED NOT NULL DEFAULT 0,
  `desc` varchar(255) CHARACTER SET utf8 COLLATE utf8_general_ci NOT NULL,
  `content` text CHARACTER SET utf8 COLLATE utf8_general_ci NOT NULL,
  `img` varchar(255) CHARACTER SET utf8 COLLATE utf8_general_ci NOT NULL,
  `add_time` datetime(0) NULL DEFAULT CURRENT_TIMESTAMP,
  `imgcategory_id` int(11) NOT NULL,
  PRIMARY KEY (`img_id`) USING BTREE,
  INDEX `img_category`(`imgcategory_id`) USING BTREE,
  CONSTRAINT `think_img_ibfk_1` FOREIGN KEY (`imgcategory_id`) REFERENCES `think_img_category` (`id`) ON DELETE CASCADE ON UPDATE CASCADE
) ENGINE = InnoDB AUTO_INCREMENT = 9 CHARACTER SET = utf8 COLLATE = utf8_general_ci ROW_FORMAT = Dynamic;

